Array.prototype.unique=function(){return this.filter(function(a,b,c){return b==c.indexOf(a)})};
HTMLElement.prototype.hasClass=function(a){return 0<=this.className.split(" ").indexOf(a)};
HTMLElement.prototype.addClass=function(a){this.className=this.className.split(" ").concat(a.split(" ")).unique().filter(Boolean).join(" ");return this};
HTMLElement.prototype.removeClass=function(a){a=a.split(" ");this.className=this.className.split(" ").filter(function(b){return 0>a.indexOf(b)}).join(" ");return this};
HTMLElement.prototype.css=function(a,b){if("object"==typeof a){for(var c in a)this.style[c]=a[c];return this}return"string"==typeof a?"undefined"!=typeof b?(this.style[a]=b,this):window.getComputedStyle(this,null)[a]:window.getComputedStyle(this,null)};
HTMLElement.prototype.height=function(a){return isNaN(a)?parseFloat(this.css("height")):(a=parseFloat(a),this.css("height",a+"px"),this)};
HTMLElement.prototype.width=function(a){return isNaN(a)?parseFloat(this.css("width")):(a=parseFloat(a),this.css("width",a+"px"),this)};